<script>
  import Api from '@/api';
  import QuestionItem from '@/components/QuestionItem';

  export default {
    data() {
      return {
        loading: true,
        questions: null
      }
    },
    components: {
      QuestionItem
    },
    watch: {
      '$route'() {
        this.loadQuestions();
